                return (descOrder ? b[field] - a[field] : a[field] - b[field]);
            } else if(type === 'string') {
                return (descOrder ? b[field].toLocaleLowerCase().localeCompare(a [field].toLocaleLowerCase()) : a[field].toLocaleLowerCase().localeCompare(b [field].toLocaleLowerCase()));
            } else if(type === 'date') {
                return (descOrder ? new Date(b[field]) - new Date(a[field]) : new Date(a[field]) - new Date(b[field]));
            }
        });
        this.setState({ filteredData: sortedData });
    }

    render() {
        const { filteredData, pageNo } = this.state;
        const dataForSplice = filteredData.concat();
        let k = 0;
        return (
            <div>
                <div id="titleId">
                    <span>Games List</span>
                </div>
                <div id="searchId">
                    <input type="text" id="searchInput" name="searchQuery" placeholder="Search" onChange={this.filterDataSet.bind(this)} />
                    <span className={"searchIcon"}></span>
                </div>
                <div className={'grid'}>
                    <table className={"table table-bordered"}>
                        <thead className={'tableHead'}>
                            <tr>
                                <td className={"headerLabel"} onClick={() => this.sortData('rank')}>
                                    <span>Rank</span>
                                </td>
                                <td className={"headerLabel"} onClick={() => this.sortData('name')}>
                                    <span>Name</span>
                                </td>
                                <td className={"headerLabel"} onClick={() => this.sortData('platform')}>
                                    <span>Platform</span>
                                </td>
                                <td className={"headerLabel"} onClick={() => this.sortData('genre')}>
                                    <span>Genre</span>
                                </td>
                                <td className={"headerLabel"} onClick={() => this.sortData('publisher')}>
                                    <span>Publisher</span>
                                </td>
                                <td className={"headerLabel"} onClick={() => this.sortData('global_sales')}>
                                    <span>Global_Sales</span>
                                </td>
                                <td className={"headerLabel"} onClick={() => this.sortData('year')}>
                                    <span>Year</span>
                                </td>
                            </tr>
                        </thead>
                        <tbody>
                            {dataForSplice.splice((pageNo - 1) * 20, 20).map(data => (
                                <tr key={k++}>
                                    <td>{data.rank}</td>
                                    <td>{data.name}</td>
                                    <td>{data.platform}</td>
                                    <td>{data.genre}</td>
                                    <td>{data.publisher}</td>
                                    <td>{data.global_sales}</td>
                                    <td>{data.year}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
                <div id="paginationId">
                    Page <input id="pageNoId" type="number" defaultValue={pageNo} name="pageNo" onKeyDown={this.handleEnter.bind(this)} /> of {Math.ceil(filteredData.length / 20)}
                </div>
            </div>
        )
    }
}
